msg.statusCode = 200;
msg.payload = {
    status: msg.statusCode,
    message: 'Online ticket validation API is operational',
    time: Date.now()
};
return msg;

msg.ok = true;

if (msg.payload.hasOwnProperty('card') && msg.payload.hasOwnProperty('ticketID')) msg.ok = false; // can't have both card and ticket ID
else if (msg.payload.hasOwnProperty('card')) {
    /* card given */
    if (
        typeof msg.payload.card === 'object'
        && typeof msg.payload.card.type === 'string'
        && typeof msg.payload.card.id === 'string'
    ) {
        if (msg.payload.card.hasOwnProperty('skipValidation')) msg.verifyCard = false; // no need to verify card
        else if (
            typeof msg.payload.card.fareType === 'number'
            && typeof msg.payload.card.balance === 'number'
            && typeof msg.payload.card.dailyExpenditure === 'number' && msg.payload.card.dailyExpenditure >= 0
            && typeof msg.payload.card.currentProduct === 'number' && msg.payload.card.currentProduct >= 0
            && typeof msg.payload.card.touchedOn === 'number' && msg.payload.card.touchedOn >= 0
            && typeof msg.payload.card.prodValidated === 'string'
            && typeof msg.payload.card.prodDuration === 'number'
            && typeof msg.payload.card.passes === 'object' && Array.isArray(msg.payload.card.passes) && msg.payload.card.passes.length <= 2
            // && typeof msg.payload.card.lastTransaction === 'object'
            // && (
            //     msg.payload.card.lastTransaction === null
            //     || (
            //         typeof msg.payload.card.lastTransaction.id === 'string'
            //         && typeof msg.payload.card.lastTransaction.timestamp === 'string'
            //         && typeof msg.payload.card.lastTransaction.type === 'number'
            //         && typeof msg.payload.card.lastTransaction.location === 'number'
            //         && typeof msg.payload.card.lastTransaction.balance === 'number'
            //     )
            // )
            && typeof msg.payload.card.prodBits === 'object' && Array.isArray(msg.payload.card.prodBits) && msg.payload.card.prodBits.length == 2
            && typeof msg.payload.card.prodBits[0] === 'string' && typeof msg.payload.card.prodBits[1] === 'string' // product bits can be up to 128 bits long so they must be transmitted as 128-bit little endian integers encoded into base64
        ) {
            let passes = msg.payload.card.passes;
            for (let i = 0; i < passes.length; i++) {
                if (
                    typeof passes[i].transactionID !== 'string'
                    || typeof passes[i].product !== 'number'
                    || !(passes[i].hasOwnProperty('duration') ^ passes[i].hasOwnProperty('expiryDate')) // exclusive OR ensures only one of the two properties exist
                    || (passes[i].hasOwnProperty('duration') && typeof passes[i].duration !== 'number')
                    || (passes[i].hasOwnProperty('expiryDate') && typeof passes[i].expiryDate !== 'string')
                ) {
                    msg.ok = false;
                    break;
                }
            }

            if (msg.ok) {
                msg.verifyCard = true; // verify card
                msg.verifyDetails = msg.payload.card;
            }
        } else msg.ok = false; // malformed msg.payload.card
    } else msg.ok = false;
} else if (typeof msg.payload.ticketID === 'string') {
    /* valid ticket ID given */
    msg.ticketID = msg.payload.ticketID; // we'll query this for cards too
    msg.verifyCard = false; // we're not even checking a card :)
} else msg.ok = false; // no ticket info given

if (typeof msg.payload.location === 'number') msg.location = msg.payload.location;
else msg.ok = false;

msg.direction = {
    entry: true,
    exit: true
};
if (msg.payload.hasOwnProperty('direction')) {
    if (typeof msg.payload.direction === 'object') {
        if (msg.payload.direction.hasOwnProperty('entry')) {
            if (typeof msg.payload.direction.entry === 'boolean')
                msg.direction.entry = msg.payload.direction.entry;
            else msg.ok = false;
        }
        if (msg.payload.direction.hasOwnProperty('exit')) {
            if (typeof msg.payload.direction.exit === 'boolean')
                msg.direction.exit = msg.payload.direction.exit;
            else msg.ok = false;
        }
    }
}
msg.direction.entryOnly = msg.direction.entry && !msg.direction.exit;
msg.direction.exitOnly = !msg.direction.entry && msg.direction.exit;

msg.timestamp = new Date(); // get current timestamp for reference

return msg;

msg.statusCode = 400;
msg.payload = {
    status: msg.statusCode,
    message: 'Invalid body',
    time: Date.now()
};
return msg;

msg.urlBase = 'http://172.17.0.4:1880';
if (msg.payload.hasOwnProperty('card')) {
    msg.urlBase += `/api/cards/${msg.payload.card.type}/${msg.payload.card.id}`;
} else msg.urlBase += `/api/tickets/${msg.payload.ticketID}`;
return msg;

msg.cardOK = (
    msg.details.fareType == msg.verifyDetails.fareType
    && msg.details.balance == msg.verifyDetails.balance
    && msg.details.dailyExpenditure == msg.verifyDetails.dailyExpenditure
    && msg.details.touchedOn == msg.verifyDetails.touchedOn
    && msg.details.currentProduct == msg.verifyDetails.currentProduct
    && msg.details.prodValidated.slice(0, -5) == msg.verifyDetails.prodValidated.slice(0, -5) // ignore millisecond part
    && msg.details.prodDuration == msg.verifyDetails.prodDuration
    && (
        (msg.details.prodBits[0] == 'AAAAAAAAAAAAAAAAAAAAAA==' && msg.details.prodBits[1] == 'AAAAAAAAAAAAAAAAAAAAAA==') // TODO: this can lead to exploitable scenarios!
        || (msg.details.prodBits[0] == msg.verifyDetails.prodBits[0] && msg.details.prodBits[1] == msg.verifyDetails.prodBits[1])
    )
);

// if (msg.cardOK) {
//     /* check last transaction */
//     if (
//         (msg.verifyDetails.lastTransaction !== null && msg.details.lastTransaction === null)
//         || (msg.verifyDetails.lastTransaction === null && msg.details.lastTransaction !== null)
//     ) msg.cardOK = false;
//     else if (msg.verifyDetails.lastTransaction !== null) {
//         msg.cardOK = (
//             msg.verifyDetails.lastTransaction.id == msg.details.lastTransaction.id
//             && msg.verifyDetails.lastTransaction.timestamp == msg.details.lastTransaction.timestamp
//             && msg.verifyDetails.lastTransaction.type == msg.details.lastTransaction.type
//             && msg.verifyDetails.lastTransaction.location == msg.details.lastTransaction.location
//             && msg.verifyDetails.lastTransaction.balance == msg.details.lastTransaction.balance
//         );
//     }
// }

if (msg.cardOK) {
    /* check passes */
    if (msg.details.passes.length != msg.verifyDetails.passes) msg.cardOK = false;
    else {
        let a = msg.verifyDetails.passes;
        let b = msg.details.passes;
        for (let i = 0; i < a.length && msg.cardOK; i++) {
            let foundB = false;
            for (let j = 0; j < b.length; j++) {
                if (b[j].transactionID === a[i].transactionID) {
                    foundB = true;

                    msg.cardOK = ((a[i].activationDate !== null) == b[j].hasOwnProperty('expiryDate'))
                        && (a[i].hasOwnProperty('duration') == b[j].hasOwnProperty('duration'))
                        && (a[i].product !== b[j].product);
                    if (msg.cardOK) {
                        if (a[i].hasOwnProperty('duration') == b[j].hasOwnProperty('duration')) {
                            msg.cardOK = a[i].duration === b[j].duration;
                        } else {
                            let expiryDateA = new Date(a[i].activationDate);
                            if (a[i].duration >= 0) {
                                expiryDateA.setDate(expiryDateA.getDate() - a[i].duration);
                            } else {
                                if (expiryDateA.getHours() >= 3) expiryDateA.setDate(expiryDateA.getDate() + 1);
                                expiryDateA.setHours(3, 0, 0, 0);
                            }
                            let expiryDateB = new Date(b[j].expiryDate);
                            msg.cardOK = expiryDateA.getTime() == expiryDateB.getTime();
                        }
                    }
                    break;
                }
            }
            if (!foundB) {
                msg.cardOK = false;
                break;
            }
        }
    }
}

return msg;

msg.statusCode = 403;
msg.payload = {
    status: msg.statusCode,
    message: {
        state: 'blocked',
        text: 'Card blocked - please contact support'
    },
    transaction: null,
    details: msg.details,
    time: Date.now()
};
return msg;

msg.payload = {
    status: 403,
    message: {
        state: 'missingTouchOn',
        text: 'Missing touch on',
        transaction: {
            'type': 3
        },
        details: msg.details,
    },
    time: Date.now()
};
return msg;

msg.statusCode = 403;
msg.payload = {
    status: msg.statusCode,
    message: {
        state: 'negBalance',
        text: 'Negative balance',
        transaction: null,
        details: msg.details,
    },
    time: Date.now()
};
return msg;

msg.details.touchedOn = msg.locDetails.minProduct;

let setValidationTimestamp = (msg.details.prodDuration == 0);
if (!setValidationTimestamp) {
    /* check if expired */
    if (msg.details.prodExpiry != 0) {
        let prodExpiry = new Date(msg.details.prodValidated);
        if (msg.details.prodExpiry < 0) {
            /* daily fare */
            if (prodExpiry.getHours() >= 3) prodExpiry.setDate(prodExpiry.getDate() + 1);
            prodExpiry.setHours(3, 0, 0, 0);
        } else prodExpiry.setMinutes(prodExpiry.getMinutes() + msg.details.prodExpiry);
    }
}
if (setValidationTimestamp) msg.details.prodValidated = msg.timestamp.toISOString();

return msg;

msg.cardExpired = false;
if (msg.details.expiryDate !== null) msg.cardExpired = new Date(msg.details.expiryDate) < msg.timestamp;

return msg;

msg.statusCode = 403;
msg.payload = {
    status: msg.statusCode,
    message: {
        state: 'expired',
        text: 'Ticket expired - time to get a new one'
    },
    transaction: null,
    details: msg.details,
    time: Date.now()
};
return msg;

msg.payload = {
    status: 200,
    message: {
        state: 'touchedOn',
        text: 'Touched on',
        transaction: {
            'type': (msg.hasOwnProperty('touchedOff')) ? 2 : 0
        },
        details: msg.details
    },
    time: Date.now()
};
return msg;

msg.payload = {
    status: 200,
    message: {
        state: 'touchedOff',
        text: 'Touched off',
        transaction: {
            'type': 1
        },
        details: msg.details
    },
    time: Date.now()
};
return msg;

msg.validatedDay = new Date(msg.details.prodValidated);
if (msg.validatedDay.getHours() < 3) msg.validatedDay.setHours(msg.validatedDay.getHours() - 1); // belonging to previous PT day
msg.validatedDay.setHours(3, 0, 0, 0);

msg.today = new Date(msg.timestamp);
if (msg.today.getHours() < 3) msg.today.setHours(msg.today.getHours() - 1); // belonging to previous PT day
msg.today.setHours(3, 0, 0, 0);

msg.prodExpired = msg.prodExpiredPrev
    = (msg.validatedDay.getTime() != msg.today.getTime()); // indicates if product has already expired (in this case, if it has expired since the previous day)

return msg;

let passIdx = -1;

for (let i = msg.details.passes.length - 1; i >= 0; i++) {
    let prodDetails = msg.details.passes[i].prodDetails;
    if (prodDetails.fromZone <= msg.minProd.details.fromZone && prodDetails.toZone >= msg.minProd.details.toZone) {
        /* suitable product found */
        passIdx = i;
        if (msg.details.passes[i].activationDate !== null) break; // we've found a pre-activated pass
    }
}

if (passIdx >= 0) {
    msg.passIdx = passIdx;
    msg.ticketValue = 2 * msg.fareDetails.productFares[msg.details.passes[passIdx].id];
}

return msg;

let activationDate = new Date(msg.timestamp);
activationDate.setHours(0, 0, 0, 0);
msg.details.passes[msg.passIdx].activationDate = activationDate.toISOString();

msg.payload = {
    date: msg.details.passes[msg.passIdx].activationDate
};
msg.passID = msg.details.passes[msg.passIdx].transactionID;

return msg;

msg.ticketValue = 2 * msg.fareDetails.productFares[
    msg.details.passes[msg.passIdx].product
];
return msg;

msg.minProd = {
    idFrom: Math.min(msg.onDetails.fromZone, msg.offDetails.fromZone),
    idTo: Math.max(msg.onDetails.toZone, msg.offDetails.toZone)
};

if (!msg.prodExpired && msg.details.currentProduct != 0) {
    /* current product not expired, so there's current product details in msg.payload */
    msg.minProd.idFrom = Math.min(msg.minProd.idFrom, msg.payload.message.fromZone);
    msg.minProd.idTo = Math.max(msg.minProd.idTo, msg.payload.message.toZone);
}

return msg;

msg.ticketValue = msg.fareDetails.productFares[
    msg.details.currentProduct
];

let prodBits2 = Buffer.from(msg.details.prodBits[1], 'base64');
let byte = Math.floor(msg.details.currentProduct / 8), bit = msg.details.currentProduct % 8;
if (prodBits2[byte] & (1 << bit)) msg.ticketValue *= 2; // daily

return msg;

let byte = Math.floor(msg.details.passes[msg.passIdx].id / 8);
let bit = msg.details.passes[msg.passIdx].id % 8;

for (let i = 0; i < 2; i++) {
    let buf = Buffer.from(msg.details.prodBits[i], 'base64');
    buf[byte] |= (1 << bit);
    msg.details.prodBits[i] = buf.toString('base64');
}

return msg;

let byte = Math.floor(msg.minProd.id / 8);
let bit = msg.minProd.id % 8;

msg.prodMarked = [false, false];

for (let i = 0; i < 2; i++) {
    let buf = Buffer.from(msg.details.prodBits[i], 'base64');
    msg.prodMarked[i] = (buf[byte] & (1 << bit)) != 0;
}

return msg;

msg.farePending = msg.fareDetails.productFares[
    msg.minProd.id
];
return msg;

let byte = Math.floor(msg.minProd.id / 8);
let bit = msg.minProd.id % 8;

let buf = Buffer.from(msg.details.prodBits[0], 'base64');
buf[byte] |= (1 << bit);
msg.details.prodBits[0] = buf.toString('base64');

return msg;

msg.farePending = 2 * msg.fareDetails.productFares[
    msg.minProd.id
];
return msg;

let byte = Math.floor(msg.minProd.id / 8);
let bit = msg.minProd.id % 8;

let buf = Buffer.from(msg.details.prodBits[1], 'base64');
buf[byte] |= (1 << bit);
msg.details.prodBits[1] = buf.toString('base64');

return msg;

msg.respPayload.message.transaction.location = msg.location;
msg.respPayload.message.transaction.balance = msg.details.balance;
if (msg.hasOwnProperty('touchedOff') && msg.details.currentProduct != msg.oldCurrentProduct)
    msg.respPayload.message.transaction.product = msg.details.currentProduct;

msg.payload = msg.respPayload.message.transaction;

return msg;

let prodExpiry = new Date(msg.details.prodValidated);

if (msg.details.prodDuration < 0) {
    /* daily fare */
    if (prodExpiry.getHours() >= 3) prodExpiry.setDate(prodExpiry.getDate() + 1);
    prodExpiry.setHours(3, 0, 0, 0);
} else {
    prodExpiry.setMinutes(prodExpiry.getMinutes() + msg.minProd.details.duration);
}

msg.prodExpired = (prodExpiry.getTime() < msg.timestamp.getTime());

return msg;
